import React, { useState } from "react";
import "boxicons";

const Navbar = () => {
  const [isShowMenu, setIsShowMenu] = useState(false);
  return (
    <div class="w-full h-16 flex justify-between items-center fixed relative">
      <div class="ml-3 flex items-center gap-1">
        <img
          src={require("../assets/images/logo.png")}
          alt=""
          class="w-15 h-9"
        />
      </div>
      {isShowMenu ? (
        <ul class="w-full h-100vh bg-cyan-100 absolute top-16 animate-fade-in-right">
          <li class="px-6 py-3 active">Home</li>
          <li class="px-6 py-3">Menu</li>
          <li class="px-6 py-3">Service</li>
          <li class="px-6 py-3">Shop</li>
        </ul>
      ) : null}
      <box-icon
        name="menu"
        class="mr-3 cursor-pointer lg:hidden sm"
        onClick={() => setIsShowMenu(!isShowMenu)}
      ></box-icon>
    </div>
  );
};

export default Navbar;
