import React from "react";
import Button from "./Button";

const Feature = ({
  title,
  subtitle,
  subtitleColor,
  content,
  image,
  reverse,
  roundedButton,
}) => {
  return (
    <div className="feature mt-[90px] px-4 grid grid-cols-1">
      <img src={image} alt="abc" />
      <div class="mt-10">
        <h4 class={`text-center ${subtitleColor} uppercase`}>{subtitle}</h4>
        <h2 class="text-center text-2xl">{title}</h2>
        <p class="mt-6 text-md text-center">{content}</p>
      </div>
      {roundedButton ? <Button name="Explore Now" /> : <Button />}
    </div>
  );
};

export default Feature;
