import React from "react";
import Button from "./Button";

const Hero = () => {
  return (
    <div className="hero grid grid-cols-1">
      <img src={require("../assets/images/image1.png")} alt="" />
      <div className="right">
        <h1 class="text-4xl">
          Groceries delivered in as little as <span> 2 hours</span>
        </h1>
        <p>
          Grocen atssures fresh grocery every morning to your family without
          getting out in this pandemic.
        </p>
      </div>
      <div className="button">
        <Button name="Order Now" />
      </div>
    </div>
  );
};

export default Hero;
